using FluentValidation;using Einsatzueberwachung.Domain.Models;namespace Einsatzueberwachung.Domain.Validators;/// <summary>/// Validator für PersonalEntry/// </summary>public class PersonalEntryValidator : AbstractValidator<PersonalEntry>{    public PersonalEntryValidator()    {        RuleFor(p => p.Vorname)            .NotEmpty().WithMessage("Vorname ist erforderlich")            .MinimumLength(2).WithMessage("Vorname muss mindestens 2 Zeichen haben")            .MaximumLength(50).WithMessage("Vorname darf maximal 50 Zeichen haben");        RuleFor(p => p.Nachname)            .NotEmpty().WithMessage("Nachname ist erforderlich")            .MinimumLength(2).WithMessage("Nachname muss mindestens 2 Zeichen haben")            .MaximumLength(50).WithMessage("Nachname darf maximal 50 Zeichen haben");        RuleFor(p => p.Telefon)            .NotEmpty().WithMessage("Telefonnummer ist erforderlich")            .Matches(@"^[\d\s\+\-\(\)\/]+$").WithMessage("Ungültiges Telefonnummer-Format")            .When(p => !string.IsNullOrWhiteSpace(p.Telefon));    }}